-- ----------------------------
-- Table structure for products
-- ----------------------------
DROP TABLE IF EXISTS `products`;
CREATE TABLE `products`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `slug` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `short_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `image` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `detail` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `ori_price` int(11) NULL DEFAULT NULL,
  `sell_price` int(11) NULL DEFAULT NULL,
  `in_stock` int(11) NULL DEFAULT NULL,
  `color` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `cpu` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `ram` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `screen_size` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `operating_sys` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `views` int(11) NULL DEFAULT NULL,
  `star` double(8, 2) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `products_slug_unique`(`slug`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 67 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of products
-- ----------------------------
INSERT INTO `products` VALUES (53, 'Xiaomi Redmi Note 7 ', 'xiaomi-redmi-note-7', 'Redmi Note 7 sản phẩm giá rẻ cận hoàn hảo nhất, màn hình giọt nước thời thượng 6.3', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd67dcd2c3e-RedmiNote7-1.png', '<h2>Redmi Note 7&nbsp;sản phẩm gi&aacute; rẻ cận ho&agrave;n hảo nhất, m&agrave;n h&igrave;nh giọt nước thời thượng 6.3, chạy con chip Snapdragon 660, Camera khủng 48 MP, Pin 4000 Mah, k&iacute;nh cường lực&nbsp;Gorilla 5, sạc nhanh 4.0, Bluetooth 5.0, cổng kết nối Type-C &hellip;</h2>\r\n\r\n<h3>Thiết kế của&nbsp;Redmi Note 7 c&oacute; g&igrave; h&oacute;t ?</h3>\r\n\r\n<p>Đ&acirc;̀u tháng này, Xiaomi tuy&ecirc;n b&ocirc;́ thương hi&ecirc;̣u Redmi sẽ được tách thành c&ocirc;ng ty con, hoạt đ&ocirc;̣ng đ&ocirc;̣c l&acirc;̣p. Và đúng như hứa hẹn trước đó, Redmi đã trình làng thi&ecirc;́t bị đ&acirc;̀u ti&ecirc;n của họ với thiết kế b&oacute;ng bầy với 3 m&agrave;u chủ đạo- đen, đỏ, xanh tất cả đều b&oacute;ng l&aacute;ng, ở mặt trước l&agrave; m&agrave;n h&igrave;nh c&oacute; tỷ lệ giọt nược với k&iacute;ch thước 6.3 icnh, gi&uacute;p cho trải nhiệm tốt hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Redmi Note 7 Camera khủng.</h3>\r\n\r\n<p>Và thứ hai, Redmi Note 7 n&ocirc;̉i b&acirc;̣t với camera sau l&ecirc;n tới 48MP, sử dụng cảm bi&ecirc;́n Samsung GM1, kh&acirc;̉u đ&ocirc;̣ f/1.8 cùng cảm bi&ecirc;́n phụ 5MP. Trong khi đó, camera trước của máy có cảm bi&ecirc;́n 13MP. Camera sau của Redmi Note 7 có th&ecirc;̉ chụp ảnh với 48 tri&ecirc;̣u đi&ecirc;̉m ảnh kích thước 0,8 micron trong đi&ecirc;̀u ki&ecirc;̣n ánh sáng t&ocirc;́t hoặc k&ecirc;́t hợp các pixel g&acirc;̀n nhau đ&ecirc;̉ tạo ra bức ảnh tương đương 12MP với kích thước đi&ecirc;̉m ảnh 1,6 micron. Cảm bi&ecirc;́n chính 48MP của Redmi Note 7 có kích thước v&acirc;̣t lý lớn hơn m&ocirc;̣t chút so với các cảm bi&ecirc;́n thường th&acirc;́y tr&ecirc;n smartphone, đi&ecirc;̀u này giúp nó thu được nhi&ecirc;̀u ánh sáng hơn. T&acirc;́t nhi&ecirc;n, phải đánh giá trực ti&ecirc;́p chúng ta mới bi&ecirc;́t ch&acirc;́t lượng ảnh của Redmi Note 7 như th&ecirc;́ nào.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-6.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Sức mạnh của Redmi Note 7 được đảm bảo bởi chip Snapdragon 660</h3>\r\n\r\n<p>Cục Pin 4.000mAh. Các th&ocirc;ng s&ocirc;́ đáng chú ý khác bao g&ocirc;̀m màn hình Full HD+ 6.3 inch với thi&ecirc;́t k&ecirc;́ giọt nước, c&ocirc;̉ng USB-C, sạc 18W và v&acirc;̃n có jack cắm tai nghe. Snap 660 đủ mạnh mẽ để chiến mọi tựa game .</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-8.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 4090000, 3990000, 100, '#000000', 'Snapdragon 660', '3GB', '6.3 inch  2340 x 1080 FHD +', 'Android 8.0', 22, NULL);
INSERT INTO `products` VALUES (56, 'Xiaomi Mi 9', 'xiaomi-mi-9', 'Ngoại hình bóng bẩy, đẳng cấp dòng Mi, Mi9 tỷ lệ màn hình lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd73d6e2c3f-Mi9-1.png', '<h2>Ngoại h&igrave;nh b&oacute;ng bẩy, đẳng cấp d&ograve;ng Mi, Mi9 tỷ lệ m&agrave;n h&igrave;nh lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời, Sạc kh&ocirc;ng d&acirc;y 20w, Camera 48MP, Hỗ trợ chơi game HDR, Cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, nhiều n&acirc;ng cấp đ&aacute;ng đồng tiền kh&aacute;c</h2>\r\n\r\n<p><strong>Ngoại h&igrave;nh: Mặt lưng ba chiều m&agrave;u xanh lam v&agrave; m&agrave;u t&iacute;m</strong></p>\r\n\r\n<p>Hiện tại, Xiaomi 9 đ&atilde; ra mắt mặt lưng ba chiều m&agrave;u xanh lam v&agrave; t&iacute;m.&nbsp;Lei Jun cho biết, th&ocirc;ng qua quy tr&igrave;nh h&igrave;nh ba chiều bằng laser ở quy m&ocirc; nano, cộng với lớp phủ hai lớp, Mi 9 sẽ hiển thị m&agrave;u cầu vồng rực rỡ v&agrave; &aacute;nh kim lấp l&aacute;nh dưới &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-6.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-16.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Bộ xử l&yacute;: được trang bị Qualcomm Snapdragon 855</strong></p>\r\n\r\n<p>Xiaomi 9 được trang bị Qualcomm Snapdragon 855 với tần số l&ecirc;n tới 2,84GHz. Snapdragon 855 sử dụng quy tr&igrave;nh 7nm v&agrave; kiến ​​tr&uacute;c Kryo 485.&nbsp;T&aacute;m l&otilde;i được chia th&agrave;nh ba nh&oacute;m, l&otilde;i si&ecirc;u lớn 2,84GHz + ba l&otilde;i lớn 2,42GHz + bốn l&otilde;i nhỏ 1,80GHz để c&acirc;n bằng hiệu năng v&agrave; mức ti&ecirc;u thụ điện năng tốt hơn.&nbsp;Tr&ecirc;n Ann Bunny, Xiaomi 9 đ&atilde; chạy được số điểm 387.851.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-12.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-18.jpg\" style=\"height:563px; width:940px\" /></p>\r\n\r\n<p><strong>M&aacute;y ảnh, Sony 48 MP</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng độ tương phản, pha v&agrave; laser để lấy n&eacute;t lai. Ba camera sử dụng động cơ v&ograve;ng k&iacute;n để cải thiện tốc độ lấy n&eacute;t v&agrave; tốc độ chụp ảnh.&nbsp;C&aacute;c th&ocirc;ng số của ba camera ph&iacute;a sau v&agrave; ống k&iacute;nh ph&iacute;a trước của Xiaomi Mi9 như sau.</p>\r\n\r\n<ul>\r\n	<li>Ống k&iacute;nh g&oacute;c rộng 48 MP, khẩu độ f / 1.75, ống k&iacute;nh 6P, cảm biến Sony IMX586, diện t&iacute;ch cảm quang lớn 1/2 inch.</li>\r\n	<li>12 MP người th&iacute;ch ống k&iacute;nh, khẩu độ f / 2.2, ống k&iacute;nh 6P, l&agrave;m mờ hậu cảnh, zoom quang 2x.</li>\r\n	<li>Ống k&iacute;nh g&oacute;c si&ecirc;u rộng 16 MP, khẩu độ f / 2.2, ống k&iacute;nh 6P, 117 độ, ống k&iacute;nh n&agrave;y cũng hỗ trợ macro 4cm.</li>\r\n	<li>Ống k&iacute;nh ph&iacute;a trước 24 MP, hỗ trợ tất cả c&aacute;c chức năng l&agrave;m đẹp của Xiaomi AI.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-13.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh: gam m&agrave;u 103,8% NTSC v&agrave; chiếm tỷ lệ m&agrave;n h&igrave;nh 90,7%</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng m&agrave;n h&igrave;nh h&agrave;ng đầu Samsung AMOLED, gam m&agrave;u si&ecirc;u rộng 103,8% NTSC, độ s&aacute;ng l&ecirc;n tới 600nit, độ tương phản cực cao 60.000: 1.&nbsp;Đồng thời, m&agrave;n h&igrave;nh cũng nhận được chứng nhận bảo vệ mắt của Viện kỹ sư điện VDE của Đức, tỷ lệ &aacute;nh s&aacute;ng xanh c&oacute; hại của m&agrave;n h&igrave;nh chỉ l&agrave; 12% v&agrave; LCD giảm 33%.&nbsp;Xiaomi Mi 9 c&oacute; tỷ lệ m&agrave;n h&igrave;nh cực cao 90,7% , cảm biến &aacute;nh s&aacute;ng dưới m&agrave;n h&igrave;nh c&oacute; độ nhạy cao v&agrave; cảm biến khoảng c&aacute;ch si&ecirc;u &acirc;m thế hệ thứ ba.&nbsp;Vỏ m&agrave;n h&igrave;nh của Xiaomi Mi 9 được l&agrave;m bằng k&iacute;nh gorilla thế hệ thứ s&aacute;u của Corning, c&oacute; khả năng chịu lực cao hơn 50% so với k&iacute;nh gorilla thế hệ thứ năm của Corning.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-14.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>V&acirc;n tay: Nhận dạng v&acirc;n tay trong m&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi Mi 9 (gọi tắt l&agrave; v&acirc;n tay tr&ecirc;n m&agrave;n h&igrave;nh, viết tắt l&agrave; FOD) ẩn cảm biến v&acirc;n tay quang học gồm ba ống k&iacute;nh dưới m&agrave;n h&igrave;nh OLED, tương tự như camera macro, với một pixel l&ecirc;n đến 5 recognitionm để tăng tốc độ nhận dạng v&acirc;n tay v&agrave; Độ ch&iacute;nh x&aacute;c.&nbsp;Xiaomi&nbsp; Mi 9 cải thiện tỷ lệ mở kh&oacute;a th&agrave;nh c&ocirc;ng trong c&aacute;c cảnh ng&oacute;n tay kh&ocirc;, nhiệt độ thấp v&agrave; &aacute;nh s&aacute;ng mặt trời trực tiếp ngo&agrave;i trời v&agrave; giảm sự kh&oacute; chịu về thị gi&aacute;c do mở kh&oacute;a r&ograve; rỉ &aacute;nh s&aacute;ng bằng c&aacute;ch giảm dấu v&acirc;n tay để mở kh&oacute;a độ s&aacute;ng của khu vực hiển thị v&agrave;o ban đ&ecirc;m hoặc trong c&aacute;c kh&ocirc;ng gian mờ.&nbsp;Lei Jun cho biết chi ph&iacute; to&agrave;n diện khi sử dụng c&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi 9 &iacute;t nhất l&agrave; 150 nh&acirc;n d&acirc;n tệ so với v&acirc;n tay điện dung truyền thống. Dấu v&acirc;n tay m&agrave;n h&igrave;nh tương đương với camera macro 5&mu;m, cao hơn nhiều lần so với gi&aacute; truyền thống v&agrave; v&acirc;n tay m&agrave;n h&igrave;nh chỉ c&oacute; thể được sử dụng với m&agrave;n h&igrave;nh OLED. Tuy nhi&ecirc;n, m&agrave;n h&igrave;nh OLED qu&aacute; đắt, dấu v&acirc;n tay ph&iacute;a sau vẫn l&agrave; sự ph&aacute;t triển về c&ocirc;ng nghệ v&agrave; l&agrave; giải ph&aacute;p hiệu quả nhất về chi ph&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-15.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Loa: Loa si&ecirc;u tuyến t&iacute;nh lớn tương đương 0,9cc</strong></p>\r\n\r\n<p>Xiaomi Mi 9 c&oacute; loa si&ecirc;u tuyến t&iacute;nh chất lượng cao 12&times;17.&nbsp;Buồng &acirc;m thanh được thiết kế bằng vật liệu Bass để đạt được khoang &acirc;m thanh lớn tương đương 0,9cc.&nbsp;Smart PA điện &aacute;p cực cao h&agrave;ng đầu . Được sử dụng để tản nhiệt cho hệ thống &acirc;m thanh để đạt được &acirc;m thanh tốt hơn.&nbsp;MiSound tự ph&aacute;t triển, điều chỉnh v&agrave; thử nghiệm chuy&ecirc;n nghiệp cho sở th&iacute;ch của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Cảm biến: NFC, GPS, Hồng ngoại</strong></p>\r\n\r\n<p>NFC của Xiaomi Mi 9 sử dụng chip SN100T thế hệ mới của NXP, c&oacute; tốc độ đọc v&agrave; giao dịch thẻ nhanh hơn, dung lượng lớn hơn v&agrave; c&oacute; thể đi k&egrave;m với nhiều thẻ (Hiện chưa được &aacute;p dụng tại việt nam) . GPS tần số k&eacute;p của Xiaomi Mi9 c&oacute; thể triệt ti&ecirc;u hiệu quả nhiễu do phản xạ t&iacute;n hiệu GPS trong th&agrave;nh phố th&ocirc;ng qua băng tần k&eacute;p L1 + L5 v&agrave; c&oacute; thể khắc phục độ trễ do t&iacute;n hiệu truyền qua tầng điện ly, tập trung v&agrave;o tối ưu h&oacute;a điều hướng người đi bộ, điều hướng đường hầm v&agrave; định vị. Nhanh hơn v&agrave; ch&iacute;nh x&aacute;c hơn.&nbsp;Ngo&agrave;i ra, Mi 9 đ&atilde; bổ sung lại điều khiển từ xa hồng ngoại.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Tối ưu h&oacute;a hệ thống: Th&ecirc;m chếđộ Game Turbo v&agrave; n&uacute;t AI</strong></p>\r\n\r\n<p>Theo Lei Jun, để ph&aacute;t huy đầy đủ hiệu năng mạnh mẽ của Snap 855, Xiaomi đ&atilde; tập trung v&agrave;o chế độ tr&ograve; chơi Game Turbo. Intellisense tr&ograve; chơi đi cảnh phức tạp như PUBG.., t&agrave;i nguy&ecirc;n hệ thống lập lịch thời gian thực, tr&aacute;nh bị lag một c&aacute;ch hiệu quả. Trong Game, bạn c&oacute; thể mở ra thanh c&ocirc;ng cụ v&agrave; xem CPU ở thời gian thực, mức sử dụng GPU v&agrave; số khung h&igrave;nh. Xiaomi Mi 9 k&iacute;ch hoạt chức năng tăng cường hiển thị khi chơi game, gi&uacute;p tăng cường độ tương phản của sương m&ugrave;, cảnh đ&ecirc;m, cỏ v&agrave; rừng. Người chơi cũng c&oacute; thể chọn h&igrave;nh ảnh s&aacute;ng hoặc sống động theo sở th&iacute;ch của m&igrave;nh. Xiaomi cũng t&ugrave;y chỉnh c&aacute;c th&ocirc;ng số &acirc;m thanh cho c&aacute;c tr&ograve; chơi kh&aacute;c nhau. Khi bạn li&ecirc;n lạc với đồng đội, bạn c&oacute; thể giảm nhiễu của &acirc;m thanh nền. Ngo&agrave;i ra Xiaomi đ&atilde; tối ưu h&oacute;a đặc biệt c&aacute;c vấn đề về cảm ứng , gi&uacute;p giảm đ&aacute;ng kể độ trễ từ cảm ứng đến phản ứng m&agrave;n h&igrave;nh v&agrave; c&oacute; thể t&ugrave;y chỉnh mức độ của tay theo c&aacute;c th&oacute;i quen vận h&agrave;nh kh&aacute;c nhau, tương tự như độ nhạy của chuột.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Xiaomi Mi9 đ&atilde; th&ecirc;m n&uacute;t AI, nhấn v&agrave; giữ để gọi trực tiếp bạn cũng c&oacute; thể t&ugrave;y chỉnh: nhấp, nhấp đ&uacute;p c&oacute; thể thiết lập c&aacute;c chức năng kh&aacute;c nhau.&nbsp;Xiaomi cũng m&ocirc; phỏng chức năng ph&iacute;m tắt Siri của Apple, th&ecirc;m &ldquo;ph&iacute;m tắt t&igrave;nh y&ecirc;u nhỏ&rdquo;, c&oacute; thể được sử dụng để gọi ra c&aacute;c chức năng ph&iacute;m tắt được sử dụng thường xuy&ecirc;n (Hiện tại mới chỉ &aacute;p dụng cho tiếng trung )</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-17.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Phi&ecirc;n bản trong suốt: Ram 12G v&agrave; nắp lưng bằng k&iacute;nh trong suốt</strong></p>\r\n\r\n<p>Xiaomi Mi 9 cũng sẽ ra mắt phi&ecirc;n bản độc quyền trong suốt, sử dụng ống k&iacute;nh&nbsp; với khẩu độ f / 1.47 của camera ch&iacute;nh 48 triệu pixel, Ram 12GB.&nbsp;B&ecirc;n dưới to&agrave;n bộ mặt sau k&iacute;nh chỉ 0,4 mm, bằng độ d&agrave;y của 4 tờ giấy A4.&nbsp;Tr&ecirc;n tấm nh&ocirc;m si&ecirc;u mỏng 0,3mm c&oacute; khả năng tản nhiệt cao,&nbsp; ở mặt sau của k&iacute;nh gradient.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-11.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 12090000, 11990000, 50, '#000000', 'Snapdragon 855', '6GB', '6.39 inch  FHD +', 'Android 9.0', 4, NULL);
INSERT INTO `products` VALUES (57, 'Black Shark 2', 'black-shark-2', 'Black Shark 2 trang bị Snapdragon 855, camera chính 48MP', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd75ef3fe7b-BlackShark2-1.png', '<h1>Black Shark 2 trang bị Snapdragon 855, camera ch&iacute;nh 48MP, 2 khay tản nhiệt chất lỏng, Pin 4000 mah, c&ocirc;ng nghệ Magic Press cho hiệu quả vượt trội hơn 3D Touch của iPhone, sạc nhanh 27W, nhiệt độ m&aacute;y kh&ocirc;ng bao giờ qu&aacute; 45 độ</h1>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Thiết kế của Black Shark 2</h3>\r\n\r\n<p>Theo Xiaomi, thiết kế của Black Shark 2 được lấy cảm hứng từ những chiếc xe đua với phong c&aacute;ch hầm hố. Mặt lưng của m&aacute;y sẽ l&agrave; vị tr&iacute; của cụm camera k&eacute;p, đ&egrave;n Flash LED v&agrave; logo Black Shark. Điều th&uacute; vị l&agrave; mặt lưng của Black Shark 2 cũng sở hữu phần đ&egrave;n LED chạy dọc th&acirc;n m&aacute;y.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-14.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>C&ograve;n thiết kế mặt trước, Black Shark 2 gần như kh&ocirc;ng thay đổi so với thế hệ tiền nhiệm. Tuy m&aacute;y vẫn c&oacute; viền m&agrave;n h&igrave;nh tr&ecirc;n/dưới d&agrave;y nhưng đ&atilde; tối ưu rất nhiều, v&agrave; tất nhi&ecirc;n l&agrave; n&oacute;i kh&ocirc;ng với thiết kế &ldquo;tai thỏ&rdquo; hay đục lỗ. Điều n&agrave;y ph&ugrave; hợp hơn cho một chiếc smartphone chuy&ecirc;n game. Ngo&agrave;i ra, phần cạnh viền của m&aacute;y sẽ được bố tr&iacute; th&ecirc;m ăng-ten 4G v&agrave; Wi-Fi gi&uacute;p tăng khả năng nhận s&oacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-17.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Black Shark 2 được Xiaomi trang bị cho m&agrave;n h&igrave;nh True View AMOLED, c&oacute; k&iacute;ch thước 6,39 inch, độ ph&acirc;n giải Full HD+ (2.340 x 1.080 pixel), sử dụng tỷ lệ khung h&igrave;nh 19,5:9 v&agrave; được t&iacute;ch hợp cảm biến v&acirc;n tay b&ecirc;n dưới. M&agrave;n h&igrave;nh của Black Shark 2 sở hữu độ nhạy cảm ứng l&ecirc;n đến 240Hz. Th&ecirc;m v&agrave;o đ&oacute;, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n hỗ trợ c&ocirc;ng nghệ Magic Press cho hiệu quả vượt trội hơn 3D Touch của iPhone. Người d&ugrave;ng c&oacute; thể chia m&agrave;n h&igrave;nh l&agrave;m 2 v&agrave; g&aacute;n lực cảm ứng kh&aacute;c nhau cho từng t&iacute;nh năng, rất th&iacute;ch hợp để l&agrave;m c&aacute;c n&uacute;t tương t&aacute;c nhanh khi chơi game. Độ trễ cảm ứng chỉ l&agrave; 43.5ms, rất thấp so với 60ms của Galaxy S10. Mặt trước m&aacute;y được trang bị loa stereo v&agrave; 2 micro để chơi game.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-6.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Về cấu h&igrave;nh, Black Shark 2</h3>\r\n\r\n<p>Về cấu h&igrave;nh, Black Shark 2 được cung cấp sức mạnh xử l&yacute; đến từ con chip cao cấp Snapdragon 855, đi k&egrave;m dung lượng RAM 8GB/12GB, c&oacute; bộ nhớ trong 128GB/256GB. Black Shark 2 c&ograve;n đi k&egrave;m với một t&iacute;nh năng đặc biệt m&agrave; Xiaomi gọi l&agrave; &ldquo;Ludicrous Mode&rdquo;. N&oacute; sẽ cho ph&eacute;p tất cả c&aacute;c l&otilde;i của CPU tập trung năng lượng để xử l&yacute; một t&aacute;c vụ duy nhất, nhờ đ&oacute; giữ cho tốc độ khung h&igrave;nh trong game lu&ocirc;n ở mức cao, ổn định v&agrave; kh&ocirc;ng bị giảm đột ngột. M&aacute;y được t&iacute;ch hợp c&ocirc;ng nghệ l&agrave;m m&aacute;t bằng chất lỏng 3.0 với 6 lớp tản nhiệt, gi&uacute;p hạ nhiệt chipset đến 14 độ. Ngo&agrave;i ra, c&ocirc;ng ty cũng sản xuất ốp lưng tản nhiệt d&agrave;nh ri&ecirc;ng cho Black Shark 2, nhờ đ&oacute; giảm th&ecirc;m 5 độ nữa. Theo Xiaomi, nhiệt độ m&aacute;y sẽ kh&ocirc;ng bao giờ vượt qu&aacute; 45 độ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-4.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-3.png\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-16.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Cuối c&ugrave;ng, Black Shark 2 sẽ hoạt động tr&ecirc;n hệ điều h&agrave;nh Android 9 Pie, c&oacute; pin dung lượng 4.000mAh hỗ trợ c&ocirc;ng nghệ sạc nhanh 27W cho ph&eacute;p người d&ugrave;ng thoải m&aacute;i chơi game trong v&ograve;ng 30 ph&uacute;t chỉ với 5 ph&uacute;t sạc pin, camera sau k&eacute;p 48MP + 13MP, khẩu độ f/1.75 v&agrave; camera selfie 20MP, khẩu độ f/2.0 ở ph&iacute;a trước,&nbsp;Người d&ugrave;ng sẽ c&oacute; 3 t&ugrave;y chọn m&agrave;u sắc l&agrave; Đen, Bạc v&agrave; Xanh lam.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/Black-shark-2-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 11990000, 11690000, 20, '#000000', 'Snapdragon 855', '6GB', 'AMOLED, 6.39\", Full HD+', 'Android 9.0 (Oreo)', 7, NULL);
INSERT INTO `products` VALUES (58, 'Redmi 7', 'redmi-7', 'Xiaomi Redmi 7 chính thức ra mắt, Sapdragon 632, màu gradient, pin4.000mAh, cam kép.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd787c1a402-Redmi7-1.png', '<h1>Xiaomi Redmi 7 ch&iacute;nh thức ra mắt, Sapdragon 632, m&agrave;u gradient, pin4.000mAh, cam k&eacute;p.</h1>\r\n\r\n<p>Về thiết kế, Redmi 7 c&oacute; m&agrave;n h&igrave;nh giọt nước c&ugrave;ng m&agrave;u sắc gradient tương tự Redmi Note 7 Pro v&agrave; Redmi Note 7. Một điểm đ&aacute;ng ch&uacute; &yacute; kh&aacute;c, Redmi 7 l&agrave; smartphone đầu ti&ecirc;n của Redmi d&ugrave;ng chip Snapdragon 632.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Redmi 7 c&oacute; m&agrave;n h&igrave;nh 6.2 inch với độ ph&acirc;n giải HD+, tỷ lệ 19:9 v&agrave; hỗ trợ 84% gam m&agrave;u NTSC. Do được chứng nhận bởi TUV Rheinland về việc giảm &aacute;nh s&aacute;ng xanh n&ecirc;n m&agrave;n h&igrave;nh Redmi 7 sẽ kh&ocirc;ng g&acirc;y kh&oacute; chịu cho mắt người d&ugrave;ng. K&iacute;nh Gorilla Glass 5 cũng được Redmi sử dụng để tăng độ bền cho m&agrave;n h&igrave;nh Redmi 7.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Redmi 7 d&ugrave;ng chip Snapdragon 632 của Qualcomm. Đ&acirc;y l&agrave; con chip từng xuất hiện tr&ecirc;n Moto G7, G7 Play của Motorola, Zenfone Max M2 của Asus, Meizu Note 8 v&agrave; Honor 8C. M&aacute;y c&oacute; 3 t&ugrave;y chọn về RAM v&agrave; ROM với những mức gi&aacute; kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-3.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Theo Xiaomi, Redmi 7 sẽ được c&agrave;i sẵn Android 9 Pie với giao diện MIUI 10 c&ugrave;ng t&ugrave;y chọn font chữ lớn cho người d&ugrave;ng lớn tuổi. Pin 4.000mAh của Redmi 7 c&oacute; thể cung cấp 400 tiếng ở chế độ chờ. Thậm ch&iacute;, Xiaomi c&ograve;n tuy&ecirc;n bố rằng Redmi 7 c&oacute; c&aacute;c t&iacute;nh năng AI hỗ trợ tiết kiệm điện, tăng thời lượng sử dụng pin th&ocirc;ng qua h&agrave;ng loạt tối ưu tr&ecirc;n khắp hệ thống. Loacủa Redmi 7 cũng ph&aacute;t ra &acirc;m thanh lớn hơn 40% so với thế hệ trước nhờ t&iacute;ch hợp amplifier PA.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Ph&iacute;a sau m&aacute;y c&oacute; camera k&eacute;p với cảm biến ch&iacute;nh 12MP khẩu độ f/1.29 v&agrave; cảm biến chiều s&acirc;u 2MP. Cảm biến ch&iacute;nh của Redmi 7 ch&iacute;nh l&agrave; cảm biến đ&atilde; từng được trang bị cho flagship Xiaomi Mi 6 ra mắt năm 2017. Ở ph&iacute;a trước m&aacute;y l&agrave; camera 8MP với c&aacute;c t&iacute;nh năng như mở kh&oacute;a bằng khu&ocirc;n mặt AI face unlock v&agrave; t&iacute;nh năng l&agrave;m đẹp AI beauty.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-pro-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng kh&aacute;c của Redmi 7 gồm hỗ trợ 4G VoLTE băng tần k&eacute;p, Bluetooth, GPS, cảm biến hồng ngoại, jack cắm tai nghe 3.5mm. M&aacute;y cũng c&oacute; lớp phủ nano chống nước của P2i tương tự Note 7 Pro.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-16.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 3990000, 3690000, 35, '#000000', 'Snapdragon 632', '2GB', '6.2 inch  HD +', 'Android 8.0', 3, NULL);
INSERT INTO `products` VALUES (59, 'Redmi Note 7 Pro', 'redmi-note-7-pro', 'Redmi Note 7 Pro sản phẩm giá rẻ cận hoàn hảo nhất, màn hình giọt nước thời thượng', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd79e8e9794-RedmiNote7Pro-1.png', '<h2>Redmi Note 7 Pro sản phẩm gi&aacute; rẻ cận ho&agrave;n hảo nhất, m&agrave;n h&igrave;nh giọt nước thời th Pượng 6.3, chạy con chip Snapdragon 675, Camera khủng 48 MP, Pin 4000 Mah, k&iacute;nh cường lực&nbsp;Gorilla 5, sạc nhanh 4.0, Bluetooth 5.0, cổng kết nối Type-C &hellip;</h2>\r\n\r\n<h3>Thiết kế của&nbsp;Redmi Note 7 Pro c&oacute; g&igrave; h&oacute;t ?</h3>\r\n\r\n<p>Đầu th&aacute;ng n&agrave;y, Xiaomi tuy&ecirc;n bố thương hiệu Redmi sẽ được t&aacute;ch th&agrave;nh c&ocirc;ng ty con, hoạt động độc lập. V&agrave; đ&uacute;ng như hứa hẹn trước đ&oacute;, Redmi đ&atilde; tr&igrave;nh l&agrave;ng thiết bị đầu ti&ecirc;n của họ với thiết kế b&oacute;ng bầy với 3 m&agrave;u chủ đạo- đen, đỏ, xanh tất cả đều b&oacute;ng l&aacute;ng, ở mặt trước l&agrave; m&agrave;n h&igrave;nh c&oacute; tỷ lệ giọt nược với k&iacute;ch thước 6.3 icnh, gi&uacute;p cho trải nhiệm tốt hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Redmi Note 7 Pro Camera khủng.</h3>\r\n\r\n<p>V&agrave; thứ hai, Redmi Note 7 nổi bật với camera sau l&ecirc;n tới 48MP, sử dụng cảm biến Samsung GM1, khẩu độ f/1.8 c&ugrave;ng cảm biến phụ 5MP. Trong khi đ&oacute;, camera trước của m&aacute;y c&oacute; cảm biến 13MP. Camera sau của Redmi Note 7 c&oacute; thể chụp ảnh với 48 triệu điểm ảnh k&iacute;ch thước 0,8 micron trong điều kiện &aacute;nh s&aacute;ng tốt hoặc kết hợp c&aacute;c pixel gần nhau để tạo ra bức ảnh tương đương 12MP với k&iacute;ch thước điểm ảnh 1,6 micron. Cảm biến ch&iacute;nh 48MP của Redmi Note 7 c&oacute; k&iacute;ch thước vật l&yacute; lớn hơn một ch&uacute;t so với c&aacute;c cảm biến thường thấy tr&ecirc;n smartphone, điều n&agrave;y gi&uacute;p n&oacute; thu được nhiều &aacute;nh s&aacute;ng hơn. Tất nhi&ecirc;n, phải đ&aacute;nh gi&aacute; trực tiếp ch&uacute;ng ta mới biết chất lượng ảnh của Redmi Note 7 như thế n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-pro-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Sức mạnh của Redmi Note 7 Pro được đảm bảo bởi chip Snapdragon 675</h3>\r\n\r\n<p>Cục Pin 4.000mAh. C&aacute;c th&ocirc;ng số đ&aacute;ng ch&uacute; &yacute; kh&aacute;c bao gồm m&agrave;n h&igrave;nh Full HD+ 6.3 inch với thiết kế giọt nước, cổng USB-C, sạc 18W v&agrave; vẫn c&oacute; jack cắm tai nghe. Snap 675 đủ mạnh mẽ để chiến mọi tựa game .</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/03/redmi-7-pro-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/01/redmi-note-7-8.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 6790000, 6490000, 80, '#000000', 'Snapdragon 675', '6GB', '6.3 inch  2340 x 1080 FHD +', 'Android 9.0', 3, NULL);
INSERT INTO `products` VALUES (60, 'Mi 9 SE', 'mi-9-se', 'Xiaomi Mi 9 SE: Thiết kế nhỏ gọn, cấu hình mạnh mẽ, 3 camera, chạy Snapdragon 712, sạc nhanh 18w, giá tầm trung.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd7adb3f04a-Mi9SE-1.png', '<h2>Xiaomi Mi 9 SE: Thiết kế nhỏ gọn, cấu h&igrave;nh mạnh mẽ, 3 camera, chạy Snapdragon 712, sạc nhanh 18w, gi&aacute; tầm trung.</h2>\r\n\r\n<p>D&ugrave; c&oacute; cấu h&igrave;nh thấp hơn, nhưng Xiaomi Mi 9 SE g&acirc;y ấn tượng mạnh với thiết kế gọn nhẹ, c&ugrave;ng gi&aacute; b&aacute;n tốt v&agrave; hấp dẫn hơn rất nhiều. Smartphone n&agrave;y được đ&aacute;nh gi&aacute; l&agrave; con &aacute;t chủ b&agrave;i của Xiaomi ở ph&acirc;n kh&uacute;c tầm trung trong năm 2019 n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-se-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Đ&aacute;nh gi&aacute; thiết kế Xiaomi Mi 9 SE: Tinh tế v&agrave; sang trọng</h3>\r\n\r\n<p>So với&nbsp;Xiaomi Mi 9&nbsp;th&igrave; Mi 9 SE c&oacute; thiết kế nhỏ gọn hơn, model n&agrave;y sẽ l&agrave; lựa chọn s&aacute;ng gi&aacute; cho c&aacute;c bạn nữ, hoặc người d&ugrave;ng th&iacute;ch c&oacute; một chiếc điện thoại nhỏ gọn, dễ d&agrave;ng nh&eacute;t t&uacute;i quần m&agrave; kh&ocirc;ng bị cấn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-se-3.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Ngo&agrave;i sự kh&aacute;c biệt về k&iacute;ch thước th&igrave; Xiaomi Mi 9 SE gi&aacute; rẻ vẫn c&oacute; m&agrave;n h&igrave;nh &lsquo;giọt nước&rsquo; ở mặt trước. Mặt lưng vẫn l&agrave; cụm 3 camera k&eacute;p, v&agrave; m&aacute;y vẫn sở hữu thiết kế với bộ khung kim loại, kết hợp c&ugrave;ng hai mặt k&iacute;nh cường lực Gorilla Glass 5 thay v&igrave; Gorilla Glass 6 như Mi 9. M&aacute;y sẽ c&oacute; 3 m&agrave;u sắc để bạn lựa chọn l&agrave; Xanh dương, T&iacute;m v&agrave; X&aacute;m</p>\r\n\r\n<h3>Xiaomi Mi 9 SE: V&acirc;n tay trong m&agrave;n h&igrave;nh l&agrave; điểm nhấn</h3>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-se-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Về khả năng hiển thị Xiaomi Mi 9 SE được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước 5.97 inches, với tỷ lệ vẫn l&agrave; 19.5:9 cho độ ph&acirc;n giải (1080 x 2340 pixels) v&agrave; mật độ điểm ảnh l&ecirc;n đến 432 ppi. Nhờ tấm nền&nbsp; Super AMOLED của Samsung n&ecirc;n m&aacute;y cho h&igrave;nh ảnh hiển thị sắc n&eacute;t với g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<h3>Đ&aacute;nh gi&aacute; camera Xiaomi Mi 9 SE: Cụm 3 camera cực chất</h3>\r\n\r\n<p>Về khả năng chụp ảnh Xiaomi Mi 9 SE được trang bị cụm 3 camera&nbsp; với đ&egrave;n Dual-LED flash trợ s&aacute;ng với độ ph&acirc;n giải lần lượt l&agrave;:</p>\r\n\r\n<ul>\r\n	<li>48 MP, f/1.8, 1/2&Prime;, 0.8&micro;m, PDAF</li>\r\n	<li>13 MP, f/2.4, (ultrawide)</li>\r\n	<li>8 MP, f/2.4, (telephoto), PDAF</li>\r\n</ul>\r\n\r\n<p>Trong khi đ&oacute; ở mặt trước l&agrave; camera phụ độ ph&acirc;n giải 20 MP, f/2.0. Camera của Mi 9 SE gi&aacute; rẻ hỗ trợ chụp HDR, panorama&hellip; v&agrave; quay phim chất lượng 4k 2160p@30fps.<br />\r\n<img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-se-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Đ&aacute;nh gi&aacute; hiệu năng Xiaomi Mi 9 SE: Qu&aacute; tốt trong tầm gi&aacute;</h3>\r\n\r\n<p>L&agrave; phi&ecirc;n bản r&uacute;t gọn n&ecirc;n Xiaomi Mi 9 SE được trang bị vi xử l&yacute; Qualcomm SDM712 Snapdragon 712 (10 nm) 8 nh&acirc;n c&oacute; tốc độ xung nhịp l&agrave; 2.3 GHz. M&aacute;y đi k&egrave;m với đồ họa Adreno 616, RAM 6GB c&ugrave;ng bộ nhớ trong ROM t&ugrave;y chọn l&agrave; 64/128 GB, đ&aacute;ng tiếc l&agrave; Mi 9 SE x&aacute;ch tay gi&aacute; rẻ kh&ocirc;ng hỗ trợ thẻ nhớ mở rộng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-se-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Cung cấp nguồn năng lượng cho Xiaomi Mi 9 SE ch&iacute;nh h&atilde;ng gi&aacute; rẻ l&agrave; vi&ecirc;n pin dung lượng 3070 mAh, c&oacute; hỗ trợ c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y 18W. Smartphone n&agrave;y sẽ chạy tr&ecirc;n hệ điều h&agrave;nh Android 9.0 (Pie) với giao diện MIUI 10 khi xuất xưởng.</p>\r\n', 8690000, 8390000, 20, '#000000', 'Snapdragon 712', '6BG', '5.97 inch   FHD +', 'Android 9.0', 3, NULL);
INSERT INTO `products` VALUES (61, 'Xiaomi Mi 9 Transparent Edition', 'xiaomi-mi-9-transparent-edition', 'Mi 9 TE giống về cấu hình nhưng ngoại hình ngầu gấp mấy lần Mi 9, Mi9 tỷ lệ màn hình lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời, Sạc không dây 20w', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd7d9c9c620-Mi9TE-1.png', '<h2>Mi 9 TE giống về cấu h&igrave;nh nhưng ngoại h&igrave;nh ngầu gấp mấy lần Mi 9, Mi9 tỷ lệ m&agrave;n h&igrave;nh lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời, Sạc kh&ocirc;ng d&acirc;y 20w, Camera 48MP, Hỗ trợ chơi game HDR, Cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, nhiều n&acirc;ng cấp đ&aacute;ng đồng tiền kh&aacute;c</h2>\r\n\r\n<p><strong>Ngoại h&igrave;nh: Mặt lưng ba chiều m&agrave;u xanh lam v&agrave; m&agrave;u t&iacute;m</strong></p>\r\n\r\n<p>Hiện tại, Xiaomi 9 đ&atilde; ra mắt mặt lưng ba chiều m&agrave;u xanh lam v&agrave; t&iacute;m.&nbsp;Lei Jun cho biết, th&ocirc;ng qua quy tr&igrave;nh h&igrave;nh ba chiều bằng laser ở quy m&ocirc; nano, cộng với lớp phủ hai lớp, Mi 9 sẽ hiển thị m&agrave;u cầu vồng rực rỡ v&agrave; &aacute;nh kim lấp l&aacute;nh dưới &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-12-1.jpg\" style=\"height:640px; width:823px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/play.jpg\" style=\"height:752px; width:1258px\" /></p>\r\n\r\n<p><strong>Bộ xử l&yacute;: được trang bị Qualcomm Snapdragon 855</strong></p>\r\n\r\n<p>Xiaomi 9 được trang bị Qualcomm Snapdragon 855 với tần số l&ecirc;n tới 2,84GHz. Snapdragon 855 sử dụng quy tr&igrave;nh 7nm v&agrave; kiến ​​tr&uacute;c Kryo 485.&nbsp;T&aacute;m l&otilde;i được chia th&agrave;nh ba nh&oacute;m, l&otilde;i si&ecirc;u lớn 2,84GHz + ba l&otilde;i lớn 2,42GHz + bốn l&otilde;i nhỏ 1,80GHz để c&acirc;n bằng hiệu năng v&agrave; mức ti&ecirc;u thụ điện năng tốt hơn.&nbsp;Tr&ecirc;n Ann Bunny, Xiaomi 9 đ&atilde; chạy được số điểm 387.851.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-12.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-18.jpg\" style=\"height:563px; width:940px\" /></p>\r\n\r\n<p><strong>M&aacute;y ảnh, Sony 48 MP</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng độ tương phản, pha v&agrave; laser để lấy n&eacute;t lai. Ba camera sử dụng động cơ v&ograve;ng k&iacute;n để cải thiện tốc độ lấy n&eacute;t v&agrave; tốc độ chụp ảnh.&nbsp;C&aacute;c th&ocirc;ng số của ba camera ph&iacute;a sau v&agrave; ống k&iacute;nh ph&iacute;a trước của Xiaomi Mi9 như sau.</p>\r\n\r\n<ul>\r\n	<li>Ống k&iacute;nh g&oacute;c rộng 48 MP, khẩu độ f / 1.75, ống k&iacute;nh 6P, cảm biến Sony IMX586, diện t&iacute;ch cảm quang lớn 1/2 inch.</li>\r\n	<li>12 MP người th&iacute;ch ống k&iacute;nh, khẩu độ f / 2.2, ống k&iacute;nh 6P, l&agrave;m mờ hậu cảnh, zoom quang 2x.</li>\r\n	<li>Ống k&iacute;nh g&oacute;c si&ecirc;u rộng 16 MP, khẩu độ f / 2.2, ống k&iacute;nh 6P, 117 độ, ống k&iacute;nh n&agrave;y cũng hỗ trợ macro 4cm.</li>\r\n	<li>Ống k&iacute;nh ph&iacute;a trước 24 MP, hỗ trợ tất cả c&aacute;c chức năng l&agrave;m đẹp của Xiaomi AI.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-7-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh: gam m&agrave;u 103,8% NTSC v&agrave; chiếm tỷ lệ m&agrave;n h&igrave;nh 90,7%</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng m&agrave;n h&igrave;nh h&agrave;ng đầu Samsung AMOLED, gam m&agrave;u si&ecirc;u rộng 103,8% NTSC, độ s&aacute;ng l&ecirc;n tới 600nit, độ tương phản cực cao 60.000: 1.&nbsp;Đồng thời, m&agrave;n h&igrave;nh cũng nhận được chứng nhận bảo vệ mắt của Viện kỹ sư điện VDE của Đức, tỷ lệ &aacute;nh s&aacute;ng xanh c&oacute; hại của m&agrave;n h&igrave;nh chỉ l&agrave; 12% v&agrave; LCD giảm 33%.&nbsp;Xiaomi Mi 9 c&oacute; tỷ lệ m&agrave;n h&igrave;nh cực cao 90,7% , cảm biến &aacute;nh s&aacute;ng dưới m&agrave;n h&igrave;nh c&oacute; độ nhạy cao v&agrave; cảm biến khoảng c&aacute;ch si&ecirc;u &acirc;m thế hệ thứ ba.&nbsp;Vỏ m&agrave;n h&igrave;nh của Xiaomi Mi 9 được l&agrave;m bằng k&iacute;nh gorilla thế hệ thứ s&aacute;u của Corning, c&oacute; khả năng chịu lực cao hơn 50% so với k&iacute;nh gorilla thế hệ thứ năm của Corning.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-14.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>V&acirc;n tay: Nhận dạng v&acirc;n tay trong m&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi Mi 9 (gọi tắt l&agrave; v&acirc;n tay tr&ecirc;n m&agrave;n h&igrave;nh, viết tắt l&agrave; FOD) ẩn cảm biến v&acirc;n tay quang học gồm ba ống k&iacute;nh dưới m&agrave;n h&igrave;nh OLED, tương tự như camera macro, với một pixel l&ecirc;n đến 5 recognitionm để tăng tốc độ nhận dạng v&acirc;n tay v&agrave; Độ ch&iacute;nh x&aacute;c.&nbsp;Xiaomi&nbsp; Mi 9 cải thiện tỷ lệ mở kh&oacute;a th&agrave;nh c&ocirc;ng trong c&aacute;c cảnh ng&oacute;n tay kh&ocirc;, nhiệt độ thấp v&agrave; &aacute;nh s&aacute;ng mặt trời trực tiếp ngo&agrave;i trời v&agrave; giảm sự kh&oacute; chịu về thị gi&aacute;c do mở kh&oacute;a r&ograve; rỉ &aacute;nh s&aacute;ng bằng c&aacute;ch giảm dấu v&acirc;n tay để mở kh&oacute;a độ s&aacute;ng của khu vực hiển thị v&agrave;o ban đ&ecirc;m hoặc trong c&aacute;c kh&ocirc;ng gian mờ.&nbsp;Lei Jun cho biết chi ph&iacute; to&agrave;n diện khi sử dụng c&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi 9 &iacute;t nhất l&agrave; 150 nh&acirc;n d&acirc;n tệ so với v&acirc;n tay điện dung truyền thống. Dấu v&acirc;n tay m&agrave;n h&igrave;nh tương đương với camera macro 5&mu;m, cao hơn nhiều lần so với gi&aacute; truyền thống v&agrave; v&acirc;n tay m&agrave;n h&igrave;nh chỉ c&oacute; thể được sử dụng với m&agrave;n h&igrave;nh OLED. Tuy nhi&ecirc;n, m&agrave;n h&igrave;nh OLED qu&aacute; đắt, dấu v&acirc;n tay ph&iacute;a sau vẫn l&agrave; sự ph&aacute;t triển về c&ocirc;ng nghệ v&agrave; l&agrave; giải ph&aacute;p hiệu quả nhất về chi ph&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-15.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Loa: Loa si&ecirc;u tuyến t&iacute;nh lớn tương đương 0,9cc</strong></p>\r\n\r\n<p>Xiaomi Mi 9 c&oacute; loa si&ecirc;u tuyến t&iacute;nh chất lượng cao 12&times;17.&nbsp;Buồng &acirc;m thanh được thiết kế bằng vật liệu Bass để đạt được khoang &acirc;m thanh lớn tương đương 0,9cc.&nbsp;Smart PA điện &aacute;p cực cao h&agrave;ng đầu . Được sử dụng để tản nhiệt cho hệ thống &acirc;m thanh để đạt được &acirc;m thanh tốt hơn.&nbsp;MiSound tự ph&aacute;t triển, điều chỉnh v&agrave; thử nghiệm chuy&ecirc;n nghiệp cho sở th&iacute;ch của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Cảm biến: NFC, GPS, Hồng ngoại</strong></p>\r\n\r\n<p>NFC của Xiaomi Mi 9 sử dụng chip SN100T thế hệ mới của NXP, c&oacute; tốc độ đọc v&agrave; giao dịch thẻ nhanh hơn, dung lượng lớn hơn v&agrave; c&oacute; thể đi k&egrave;m với nhiều thẻ (Hiện chưa được &aacute;p dụng tại việt nam) . GPS tần số k&eacute;p của Xiaomi Mi9 c&oacute; thể triệt ti&ecirc;u hiệu quả nhiễu do phản xạ t&iacute;n hiệu GPS trong th&agrave;nh phố th&ocirc;ng qua băng tần k&eacute;p L1 + L5 v&agrave; c&oacute; thể khắc phục độ trễ do t&iacute;n hiệu truyền qua tầng điện ly, tập trung v&agrave;o tối ưu h&oacute;a điều hướng người đi bộ, điều hướng đường hầm v&agrave; định vị. Nhanh hơn v&agrave; ch&iacute;nh x&aacute;c hơn.&nbsp;Ngo&agrave;i ra, Mi 9 đ&atilde; bổ sung lại điều khiển từ xa hồng ngoại.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Tối ưu h&oacute;a hệ thống: Th&ecirc;m chếđộ Game Turbo v&agrave; n&uacute;t AI</strong></p>\r\n\r\n<p>Theo Lei Jun, để ph&aacute;t huy đầy đủ hiệu năng mạnh mẽ của Snap 855, Xiaomi đ&atilde; tập trung v&agrave;o chế độ tr&ograve; chơi Game Turbo. Intellisense tr&ograve; chơi đi cảnh phức tạp như PUBG.., t&agrave;i nguy&ecirc;n hệ thống lập lịch thời gian thực, tr&aacute;nh bị lag một c&aacute;ch hiệu quả. Trong Game, bạn c&oacute; thể mở ra thanh c&ocirc;ng cụ v&agrave; xem CPU ở thời gian thực, mức sử dụng GPU v&agrave; số khung h&igrave;nh. Xiaomi Mi 9 k&iacute;ch hoạt chức năng tăng cường hiển thị khi chơi game, gi&uacute;p tăng cường độ tương phản của sương m&ugrave;, cảnh đ&ecirc;m, cỏ v&agrave; rừng. Người chơi cũng c&oacute; thể chọn h&igrave;nh ảnh s&aacute;ng hoặc sống động theo sở th&iacute;ch của m&igrave;nh. Xiaomi cũng t&ugrave;y chỉnh c&aacute;c th&ocirc;ng số &acirc;m thanh cho c&aacute;c tr&ograve; chơi kh&aacute;c nhau. Khi bạn li&ecirc;n lạc với đồng đội, bạn c&oacute; thể giảm nhiễu của &acirc;m thanh nền. Ngo&agrave;i ra Xiaomi đ&atilde; tối ưu h&oacute;a đặc biệt c&aacute;c vấn đề về cảm ứng , gi&uacute;p giảm đ&aacute;ng kể độ trễ từ cảm ứng đến phản ứng m&agrave;n h&igrave;nh v&agrave; c&oacute; thể t&ugrave;y chỉnh mức độ của tay theo c&aacute;c th&oacute;i quen vận h&agrave;nh kh&aacute;c nhau, tương tự như độ nhạy của chuột.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Xiaomi Mi9 đ&atilde; th&ecirc;m n&uacute;t AI, nhấn v&agrave; giữ để gọi trực tiếp bạn cũng c&oacute; thể t&ugrave;y chỉnh: nhấp, nhấp đ&uacute;p c&oacute; thể thiết lập c&aacute;c chức năng kh&aacute;c nhau.&nbsp;Xiaomi cũng m&ocirc; phỏng chức năng ph&iacute;m tắt Siri của Apple, th&ecirc;m &ldquo;ph&iacute;m tắt t&igrave;nh y&ecirc;u nhỏ&rdquo;, c&oacute; thể được sử dụng để gọi ra c&aacute;c chức năng ph&iacute;m tắt được sử dụng thường xuy&ecirc;n (Hiện tại mới chỉ &aacute;p dụng cho tiếng trung )</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-17.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Phi&ecirc;n bản trong suốt: Ram 12G v&agrave; nắp lưng bằng k&iacute;nh trong suốt</strong></p>\r\n\r\n<p>Xiaomi Mi 9 cũng sẽ ra mắt phi&ecirc;n bản độc quyền trong suốt, sử dụng ống k&iacute;nh&nbsp; với khẩu độ f / 1.47 của camera ch&iacute;nh 48 triệu pixel, Ram 12GB.&nbsp;B&ecirc;n dưới to&agrave;n bộ mặt sau k&iacute;nh chỉ 0,4 mm, bằng độ d&agrave;y của 4 tờ giấy A4.&nbsp;Tr&ecirc;n tấm nh&ocirc;m si&ecirc;u mỏng 0,3mm c&oacute; khả năng tản nhiệt cao,&nbsp; ở mặt sau của k&iacute;nh gradient.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-11.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 18990000, 18790000, 10, '#000000', 'Snapdragon 855', '6GB', '6.39 inch   FHD +', 'Android 9.0', 5, NULL);
INSERT INTO `products` VALUES (62, 'Mi 9', 'mi-9', 'Ngoại hình bóng bẩy, đẳng cấp dòng Mi, Mi9 tỷ lệ màn hình lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd7e493cfe6-Mi9-1.png', '<h2>Ngoại h&igrave;nh b&oacute;ng bẩy, đẳng cấp d&ograve;ng Mi, Mi9 tỷ lệ m&agrave;n h&igrave;nh lớn hơn 90%, chạy con chip mạnh mẽ Snap 855 tối ưu tuyệt vời, Sạc kh&ocirc;ng d&acirc;y 20w, Camera 48MP, Hỗ trợ chơi game HDR, Cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, nhiều n&acirc;ng cấp đ&aacute;ng đồng tiền kh&aacute;c</h2>\r\n\r\n<p><strong>Ngoại h&igrave;nh: Mặt lưng ba chiều m&agrave;u xanh lam v&agrave; m&agrave;u t&iacute;m</strong></p>\r\n\r\n<p>Hiện tại, Xiaomi 9 đ&atilde; ra mắt mặt lưng ba chiều m&agrave;u xanh lam v&agrave; t&iacute;m.&nbsp;Lei Jun cho biết, th&ocirc;ng qua quy tr&igrave;nh h&igrave;nh ba chiều bằng laser ở quy m&ocirc; nano, cộng với lớp phủ hai lớp, Mi 9 sẽ hiển thị m&agrave;u cầu vồng rực rỡ v&agrave; &aacute;nh kim lấp l&aacute;nh dưới &aacute;nh s&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-6.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-16.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Bộ xử l&yacute;: được trang bị Qualcomm Snapdragon 855</strong></p>\r\n\r\n<p>Xiaomi 9 được trang bị Qualcomm Snapdragon 855 với tần số l&ecirc;n tới 2,84GHz. Snapdragon 855 sử dụng quy tr&igrave;nh 7nm v&agrave; kiến ​​tr&uacute;c Kryo 485.&nbsp;T&aacute;m l&otilde;i được chia th&agrave;nh ba nh&oacute;m, l&otilde;i si&ecirc;u lớn 2,84GHz + ba l&otilde;i lớn 2,42GHz + bốn l&otilde;i nhỏ 1,80GHz để c&acirc;n bằng hiệu năng v&agrave; mức ti&ecirc;u thụ điện năng tốt hơn.&nbsp;Tr&ecirc;n Ann Bunny, Xiaomi 9 đ&atilde; chạy được số điểm 387.851.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-12.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-18.jpg\" style=\"height:563px; width:940px\" /></p>\r\n\r\n<p><strong>M&aacute;y ảnh, Sony 48 MP</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng độ tương phản, pha v&agrave; laser để lấy n&eacute;t lai. Ba camera sử dụng động cơ v&ograve;ng k&iacute;n để cải thiện tốc độ lấy n&eacute;t v&agrave; tốc độ chụp ảnh.&nbsp;C&aacute;c th&ocirc;ng số của ba camera ph&iacute;a sau v&agrave; ống k&iacute;nh ph&iacute;a trước của Xiaomi Mi9 như sau.</p>\r\n\r\n<ul>\r\n	<li>Ống k&iacute;nh g&oacute;c rộng 48 MP, khẩu độ f / 1.75, ống k&iacute;nh 6P, cảm biến Sony IMX586, diện t&iacute;ch cảm quang lớn 1/2 inch.</li>\r\n	<li>12 MP người th&iacute;ch ống k&iacute;nh, khẩu độ f / 2.2, ống k&iacute;nh 6P, l&agrave;m mờ hậu cảnh, zoom quang 2x.</li>\r\n	<li>Ống k&iacute;nh g&oacute;c si&ecirc;u rộng 16 MP, khẩu độ f / 2.2, ống k&iacute;nh 6P, 117 độ, ống k&iacute;nh n&agrave;y cũng hỗ trợ macro 4cm.</li>\r\n	<li>Ống k&iacute;nh ph&iacute;a trước 24 MP, hỗ trợ tất cả c&aacute;c chức năng l&agrave;m đẹp của Xiaomi AI.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-13.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>M&agrave;n h&igrave;nh: gam m&agrave;u 103,8% NTSC v&agrave; chiếm tỷ lệ m&agrave;n h&igrave;nh 90,7%</strong></p>\r\n\r\n<p>Xiaomi 9 sử dụng m&agrave;n h&igrave;nh h&agrave;ng đầu Samsung AMOLED, gam m&agrave;u si&ecirc;u rộng 103,8% NTSC, độ s&aacute;ng l&ecirc;n tới 600nit, độ tương phản cực cao 60.000: 1.&nbsp;Đồng thời, m&agrave;n h&igrave;nh cũng nhận được chứng nhận bảo vệ mắt của Viện kỹ sư điện VDE của Đức, tỷ lệ &aacute;nh s&aacute;ng xanh c&oacute; hại của m&agrave;n h&igrave;nh chỉ l&agrave; 12% v&agrave; LCD giảm 33%.&nbsp;Xiaomi Mi 9 c&oacute; tỷ lệ m&agrave;n h&igrave;nh cực cao 90,7% , cảm biến &aacute;nh s&aacute;ng dưới m&agrave;n h&igrave;nh c&oacute; độ nhạy cao v&agrave; cảm biến khoảng c&aacute;ch si&ecirc;u &acirc;m thế hệ thứ ba.&nbsp;Vỏ m&agrave;n h&igrave;nh của Xiaomi Mi 9 được l&agrave;m bằng k&iacute;nh gorilla thế hệ thứ s&aacute;u của Corning, c&oacute; khả năng chịu lực cao hơn 50% so với k&iacute;nh gorilla thế hệ thứ năm của Corning.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-14.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>V&acirc;n tay: Nhận dạng v&acirc;n tay trong m&agrave;n h&igrave;nh</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi Mi 9 (gọi tắt l&agrave; v&acirc;n tay tr&ecirc;n m&agrave;n h&igrave;nh, viết tắt l&agrave; FOD) ẩn cảm biến v&acirc;n tay quang học gồm ba ống k&iacute;nh dưới m&agrave;n h&igrave;nh OLED, tương tự như camera macro, với một pixel l&ecirc;n đến 5 recognitionm để tăng tốc độ nhận dạng v&acirc;n tay v&agrave; Độ ch&iacute;nh x&aacute;c.&nbsp;Xiaomi&nbsp; Mi 9 cải thiện tỷ lệ mở kh&oacute;a th&agrave;nh c&ocirc;ng trong c&aacute;c cảnh ng&oacute;n tay kh&ocirc;, nhiệt độ thấp v&agrave; &aacute;nh s&aacute;ng mặt trời trực tiếp ngo&agrave;i trời v&agrave; giảm sự kh&oacute; chịu về thị gi&aacute;c do mở kh&oacute;a r&ograve; rỉ &aacute;nh s&aacute;ng bằng c&aacute;ch giảm dấu v&acirc;n tay để mở kh&oacute;a độ s&aacute;ng của khu vực hiển thị v&agrave;o ban đ&ecirc;m hoặc trong c&aacute;c kh&ocirc;ng gian mờ.&nbsp;Lei Jun cho biết chi ph&iacute; to&agrave;n diện khi sử dụng c&ocirc;ng nghệ v&acirc;n tay m&agrave;n h&igrave;nh của Xiaomi 9 &iacute;t nhất l&agrave; 150 nh&acirc;n d&acirc;n tệ so với v&acirc;n tay điện dung truyền thống. Dấu v&acirc;n tay m&agrave;n h&igrave;nh tương đương với camera macro 5&mu;m, cao hơn nhiều lần so với gi&aacute; truyền thống v&agrave; v&acirc;n tay m&agrave;n h&igrave;nh chỉ c&oacute; thể được sử dụng với m&agrave;n h&igrave;nh OLED. Tuy nhi&ecirc;n, m&agrave;n h&igrave;nh OLED qu&aacute; đắt, dấu v&acirc;n tay ph&iacute;a sau vẫn l&agrave; sự ph&aacute;t triển về c&ocirc;ng nghệ v&agrave; l&agrave; giải ph&aacute;p hiệu quả nhất về chi ph&iacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-15.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Loa: Loa si&ecirc;u tuyến t&iacute;nh lớn tương đương 0,9cc</strong></p>\r\n\r\n<p>Xiaomi Mi 9 c&oacute; loa si&ecirc;u tuyến t&iacute;nh chất lượng cao 12&times;17.&nbsp;Buồng &acirc;m thanh được thiết kế bằng vật liệu Bass để đạt được khoang &acirc;m thanh lớn tương đương 0,9cc.&nbsp;Smart PA điện &aacute;p cực cao h&agrave;ng đầu . Được sử dụng để tản nhiệt cho hệ thống &acirc;m thanh để đạt được &acirc;m thanh tốt hơn.&nbsp;MiSound tự ph&aacute;t triển, điều chỉnh v&agrave; thử nghiệm chuy&ecirc;n nghiệp cho sở th&iacute;ch của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Cảm biến: NFC, GPS, Hồng ngoại</strong></p>\r\n\r\n<p>NFC của Xiaomi Mi 9 sử dụng chip SN100T thế hệ mới của NXP, c&oacute; tốc độ đọc v&agrave; giao dịch thẻ nhanh hơn, dung lượng lớn hơn v&agrave; c&oacute; thể đi k&egrave;m với nhiều thẻ (Hiện chưa được &aacute;p dụng tại việt nam) . GPS tần số k&eacute;p của Xiaomi Mi9 c&oacute; thể triệt ti&ecirc;u hiệu quả nhiễu do phản xạ t&iacute;n hiệu GPS trong th&agrave;nh phố th&ocirc;ng qua băng tần k&eacute;p L1 + L5 v&agrave; c&oacute; thể khắc phục độ trễ do t&iacute;n hiệu truyền qua tầng điện ly, tập trung v&agrave;o tối ưu h&oacute;a điều hướng người đi bộ, điều hướng đường hầm v&agrave; định vị. Nhanh hơn v&agrave; ch&iacute;nh x&aacute;c hơn.&nbsp;Ngo&agrave;i ra, Mi 9 đ&atilde; bổ sung lại điều khiển từ xa hồng ngoại.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Tối ưu h&oacute;a hệ thống: Th&ecirc;m chếđộ Game Turbo v&agrave; n&uacute;t AI</strong></p>\r\n\r\n<p>Theo Lei Jun, để ph&aacute;t huy đầy đủ hiệu năng mạnh mẽ của Snap 855, Xiaomi đ&atilde; tập trung v&agrave;o chế độ tr&ograve; chơi Game Turbo. Intellisense tr&ograve; chơi đi cảnh phức tạp như PUBG.., t&agrave;i nguy&ecirc;n hệ thống lập lịch thời gian thực, tr&aacute;nh bị lag một c&aacute;ch hiệu quả. Trong Game, bạn c&oacute; thể mở ra thanh c&ocirc;ng cụ v&agrave; xem CPU ở thời gian thực, mức sử dụng GPU v&agrave; số khung h&igrave;nh. Xiaomi Mi 9 k&iacute;ch hoạt chức năng tăng cường hiển thị khi chơi game, gi&uacute;p tăng cường độ tương phản của sương m&ugrave;, cảnh đ&ecirc;m, cỏ v&agrave; rừng. Người chơi cũng c&oacute; thể chọn h&igrave;nh ảnh s&aacute;ng hoặc sống động theo sở th&iacute;ch của m&igrave;nh. Xiaomi cũng t&ugrave;y chỉnh c&aacute;c th&ocirc;ng số &acirc;m thanh cho c&aacute;c tr&ograve; chơi kh&aacute;c nhau. Khi bạn li&ecirc;n lạc với đồng đội, bạn c&oacute; thể giảm nhiễu của &acirc;m thanh nền. Ngo&agrave;i ra Xiaomi đ&atilde; tối ưu h&oacute;a đặc biệt c&aacute;c vấn đề về cảm ứng , gi&uacute;p giảm đ&aacute;ng kể độ trễ từ cảm ứng đến phản ứng m&agrave;n h&igrave;nh v&agrave; c&oacute; thể t&ugrave;y chỉnh mức độ của tay theo c&aacute;c th&oacute;i quen vận h&agrave;nh kh&aacute;c nhau, tương tự như độ nhạy của chuột.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Xiaomi Mi9 đ&atilde; th&ecirc;m n&uacute;t AI, nhấn v&agrave; giữ để gọi trực tiếp bạn cũng c&oacute; thể t&ugrave;y chỉnh: nhấp, nhấp đ&uacute;p c&oacute; thể thiết lập c&aacute;c chức năng kh&aacute;c nhau.&nbsp;Xiaomi cũng m&ocirc; phỏng chức năng ph&iacute;m tắt Siri của Apple, th&ecirc;m &ldquo;ph&iacute;m tắt t&igrave;nh y&ecirc;u nhỏ&rdquo;, c&oacute; thể được sử dụng để gọi ra c&aacute;c chức năng ph&iacute;m tắt được sử dụng thường xuy&ecirc;n (Hiện tại mới chỉ &aacute;p dụng cho tiếng trung )</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-17.jpg\" style=\"height:640px; width:940px\" /><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><strong>Phi&ecirc;n bản trong suốt: Ram 12G v&agrave; nắp lưng bằng k&iacute;nh trong suốt</strong></p>\r\n\r\n<p>Xiaomi Mi 9 cũng sẽ ra mắt phi&ecirc;n bản độc quyền trong suốt, sử dụng ống k&iacute;nh&nbsp; với khẩu độ f / 1.47 của camera ch&iacute;nh 48 triệu pixel, Ram 12GB.&nbsp;B&ecirc;n dưới to&agrave;n bộ mặt sau k&iacute;nh chỉ 0,4 mm, bằng độ d&agrave;y của 4 tờ giấy A4.&nbsp;Tr&ecirc;n tấm nh&ocirc;m si&ecirc;u mỏng 0,3mm c&oacute; khả năng tản nhiệt cao,&nbsp; ở mặt sau của k&iacute;nh gradient.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2019/02/mi-9-11.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 11990000, 11590000, 40, '#000000', 'Snapdragon 855', '6GB', '6.39 inch   FHD +', 'Android 9.0', 3, NULL);
INSERT INTO `products` VALUES (63, 'Redmi 6A', 'redmi-6a', 'Redmi 6A là chiếc điện thoại thông minh thuộc phân khúc giá rẻ của Xiaomi nhưng được trang bị cấu hình mạnh mẽ và công nghệ AI hiện đại.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd7fb65c36e-Redmi6A-1.png', '<h2><a href=\"https://mistore.com.vn/redmi-6a-dgw/\">Redmi 6A</a>&nbsp;l&agrave; chiếc điện thoại th&ocirc;ng minh thuộc ph&acirc;n kh&uacute;c gi&aacute; rẻ của Xiaomi nhưng được trang bị cấu h&igrave;nh mạnh mẽ v&agrave; c&ocirc;ng nghệ AI hiện đại.</h2>\r\n\r\n<blockquote>\r\n<p><em><strong>Redmi 6A</strong>&nbsp;&agrave; bản r&uacute;t gọn của Redmi 6, với&nbsp; vi xử l&yacute; MediaTek Helio A22, c&ugrave;ng xung nhịp 2GHz, tiến tr&igrave;nh 12nm nhưng số nh&acirc;n chỉ l&agrave; 4. M&aacute;y c&oacute; RAM 2GB v&agrave; bộ nhớ trong 16GB.</em></p>\r\n</blockquote>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a-3.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Do kh&ocirc;ng c&oacute; cảm biến v&acirc;n tay n&ecirc;n nhận diện khu&ocirc;n mặt l&agrave; c&aacute;ch mở kh&oacute;a sinh trắc học duy nhất của Redmi 6A.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế điện thoại Xiaomi Redmi 6A</h3>\r\n\r\n<p><strong>Xiaomi Redmi 6A</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh 5.45 inch độ ph&acirc;n giải HD+, theo tỷ lệ 18:9 c&ugrave;ng c&aacute;c t&ugrave;y chọn m&agrave;u sắc như Platinum Silver Gray, Rose Gold, Bali Blue v&agrave; Quicksand Gold.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a-4.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>REdmi 6A được trang bị Camera với c&ocirc;ng nghệ AI gi&uacute;p chụp ảnh đẹp hơn, X&oacute;a ph&ocirc;ng cả trước v&agrave; sau</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Thiết kế mặt trước bắt kịp xu thế, c&oacute; c&ocirc;ng nghệ&nbsp; Ai hỗ trợ l&agrave;m đẹp.</p>\r\n\r\n<p>Thiết kế của&nbsp;<a href=\"https://mistore.com.vn/redmi-6a/\">redmi 6a</a>&nbsp;ở mặt sau vẫn giống redmi 5a cho đến khi ch&uacute;ng ta nh&igrave;n l&ecirc;n cụm camera đặt ngang với cam sau l&agrave; 13 MP c&ograve;n cam trước 5 MP ở mặt trước đ&atilde; được thay đổi ho&agrave;n to&agrave;n về thiết kế, m&agrave;n h&igrave;nh được l&agrave;m cong 2.5D v&agrave; c&oacute; tỷ lệ bắt kịp xu thế 18:9 với c&ocirc;ng nghẹ AI t&iacute;nh hợp theo hỗ trợ l&agrave;m đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a-5.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Cấu h&igrave;nh Xiaomi Redmi 6A</h3>\r\n\r\n<p>Cung cấp sức mạnh cho Redmi 6A l&agrave; vi xử l&yacute; Helio A22 được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh 16mm với 8 nh&acirc;n, xung nhịp 2GHz, 2GB RAM, 16GB bộ nhớ trong v&agrave; vi&ecirc;n pin 3000mAh.&nbsp;<img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Camera Xiaomi Redmi 6A</h3>\r\n\r\n<p>Thiết bị sẽ c&oacute; camera ch&iacute;nh 13MP v&agrave; camera trước 5MP, cả hai camera n&agrave;y đều hỗ trợ chức năng chụp ảnh x&oacute;a ph&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/06/redmi-6a-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 2090000, 1999000, 120, '#000000', 'Helio A22', '2GB', '5.45 inch Full HD', 'Android 8.0', 4, NULL);
INSERT INTO `products` VALUES (64, 'Mi Mix 3', 'mi-mix-3', 'Xiaomi Mi Mix 3 DGW có nắp trượt,Có sẵn tiếng việt, Camera trước kép 24MP + 2MP, Ram 10GB, hỗ trợ 5G, Màn hình hiện thị chiến tỷ lệ 93.4%, bản đặt biệt tặng kèm sạc không dây Qi.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd810d36550-MiMix3-1.png', '<ul>\r\n	<li><a href=\"https://mistore.com.vn/mi-mix-3-dgw/#dacdiem\">Đặc điểm</a></li>\r\n</ul>\r\n\r\n<h2><a href=\"https://mistore.com.vn/mi-mix-3/\">Xiaomi Mi Mix 3 DGW</a>&nbsp;c&oacute; nắp trượt,C&oacute; sẵn tiếng việt, Camera trước k&eacute;p 24MP + 2MP, Ram 10GB, hỗ trợ 5G, M&agrave;n h&igrave;nh hiện thị chiến tỷ lệ 93.4%, bản đặt biệt tặng k&egrave;m sạc kh&ocirc;ng d&acirc;y Qi.</h2>\r\n\r\n<p>Mi MIX 3 với nhiều c&ocirc;ng nghệ, t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute;. Xiaomi tự h&agrave;o khoe&nbsp;<a href=\"https://mistore.com.vn/mi-mix-3/\">Mi MIX 3</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh gần như chiếm 100% mặt trước v&agrave; trải nghệm nhiếp ảnh tối thượng.</p>\r\n\r\n<p><img alt=\"/mi-mix-3/ \" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-11.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Để c&oacute; m&agrave;n h&igrave;nh chiếm 93,4% mặt trước, loại bỏ thiết kế tai thỏ, Xiaomi đ&atilde; chọn &aacute;p dụng thiết kế trượt nhằm giải ph&oacute;ng kh&ocirc;ng gian cạnh tr&ecirc;n. Giờ đ&acirc;y, camera trước v&agrave; c&aacute;c cảm biến cũng như loa sẽ được đặt b&ecirc;n nửa dưới của Mi MIX 3 v&agrave; người d&ugrave;ng phải trượt m&aacute;y l&ecirc;n khi cần d&ugrave;ng. M&aacute;y cũng sẽ c&oacute; hai loa ở ph&iacute;a trước, tăng cường trải nghiệm &acirc;m thanh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-6.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Cạnh dưới của Mi MIX 3 hay c&ograve;n gọi l&agrave; c&aacute;i cằm được giảm k&iacute;ch thước xuống chỉ c&ograve;n 4,46 mm, tăng tỷ lệ m&agrave;n h&igrave;nh l&ecirc;n 3,82% so với Mi MIX 2S. Thiết kế ăng-ten cũng được tối ưu, giảm khoảng kh&ocirc;ng gian d&agrave;nh cho ăng-ten đi 72%.</p>\r\n\r\n<h3><strong>Về m&agrave;n h&igrave;nh&nbsp;&nbsp;Xiaomi Mi Mix 3</strong></h3>\r\n\r\n<p>Mi MIX 3 sử dụng m&agrave;n AMOLED k&iacute;ch thước 6,39 inch, độ ph&acirc;n giải Full HD+ (2340 x 1080 pixel), tỷ lệ 19.5:9. M&agrave;n h&igrave;nh n&agrave;y c&oacute; độ phủ gam m&agrave;u NTSC ở mức 103,8%, độ tương phản 60.000:1 v&agrave; độ s&aacute;ng tối đa 600 nit. C&aacute;c chế độ của m&agrave;n h&igrave;nh rất đa dạng, bao gồm chế độ ngo&agrave;i trời nắng, chế độ đọc s&aacute;ch v&agrave; khả năng tự động điều chỉnh nhiệt độ m&agrave;u một c&aacute;ch mượt m&agrave;, liền mạch.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-1.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3><strong>Về cơ chế trượt điện thoại Mi Mix 3</strong></h3>\r\n\r\n<p>thay v&igrave; chọn c&aacute;ch tự động trượt với sự hỗ trợ bởi motor như Oppo Find X, Xiaomi trang bị cho Mi MIX 3 cơ chế trượt thủ c&ocirc;ng bằng nam ch&acirc;m. Xiaomi đặt t&ecirc;n cho thiết kế trượt tr&ecirc;n Mi MIX 3 l&agrave; Magnetic Slider. Ngo&agrave;i việc giải ph&oacute;ng kh&ocirc;ng gian cạnh tr&ecirc;n, thiết kế trượt c&ograve;n mang lại cho Mi MIX 3 những trải nghiệm sử dụng mới.</p>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể trượt để trả lời cuộc gọi, trượt để chụp ảnh selfie, trượt để mở giao diện truy cập nhanh c&aacute;c ứng dụng ưa th&iacute;ch v&agrave; c&oacute; thể thiết lập trượt để mở bất kỳ ứng dụng n&agrave;o. Ngo&agrave;i ra, Xiaomi c&ograve;n cung cấp 5 hiệu ứng &acirc;m thanh trượt kh&aacute;c nhau cho người d&ugrave;ng chọn lựa.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3><strong>Về camera&nbsp;Xiaomi Mi Mix 3</strong></h3>\r\n\r\n<p>Mi MIX 3 tiếp tục c&oacute; cặp camera 12 MP + 12 MP ở ph&iacute;a sau. Camera g&oacute;c rộng c&oacute; cảm biến Sony IMX363, khẩu độ f/1.8, k&iacute;ch thước điểm ảnh 1.4&micro;m c&ugrave;ng khả năng tự động lấy n&eacute;t Dual Pixel. Trong khi đ&oacute;, camera chụp xa c&oacute; cảm biến Samsung S5K3M3+, khẩu độ f/2.4, k&iacute;ch thước điểm ảnh&nbsp;1.0&micro;m. C&aacute;c t&iacute;nh năng kh&aacute;c của cụm camera n&agrave;y bao gồm zoom quang học, ổn định h&igrave;nh ảnh OIS 4 trục, chụp ch&acirc;n dung, nhận dạng khung cảnh bằng AI.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-12.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Ở ph&iacute;a trước, Mi MIX 3 c&oacute; cặp camera 24 MP + 2 MP. Camera ch&iacute;nh d&ugrave;ng cảm biến Sony IMX576 24 MP, cho ảnh độ ph&acirc;n giải cao khi chụp ban ng&agrave;y v&agrave; ảnh Super Pixel k&iacute;ch thước điểm ảnh 1.8&micro;m khi chụp ban đ&ecirc;m. Camera thứ hai l&agrave; camera DOF.</p>\r\n\r\n<p>Camera của Mi MIX 3 được DoXMark chấm 103 điểm. Trong đ&oacute;, chụp ảnh đạt 108 điểm c&ograve;n video đạt 93 điểm. Với điểm số cao như vậy, Mi MIX 3 vượt xa so với Mi 8 v&agrave; Mi MIX 2S.&nbsp;Một t&iacute;nh năng nổi bật kh&aacute;c của camera tr&ecirc;n<a href=\"https://mistore.com.vn/mi-mix-3/\">&nbsp;Mi Mix 3</a>, đ&oacute; l&agrave; khả năng quay video slo-mo với tốc độ khung h&igrave;nh l&ecirc;n đến 960 fps.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, Xiaomi c&ograve;n tr&igrave;nh l&agrave;ng bản Mi MIX 3 đặc biệt mang t&ecirc;n The Palace Museum Edition. Ngo&agrave;i logo sư tử nổi bật, Mi MIX 3 đặc biệt c&ograve;n c&oacute; bộ nhớ RAM l&ecirc;n tới 10 GB, dung lượng lưu trữ 256 GB , C&aacute;c phi&ecirc;n bản Mi MIX 3 đều d&ugrave;ng chip Snapdragon 845 v&agrave; hỗ trợ sạc kh&ocirc;ng d&acirc;y. Tốc độ sạc kh&ocirc;ng d&acirc;y tr&ecirc;n Mi MIX 3 nhanh hơn 30% so với phi&ecirc;n bản trước v&agrave; mỗi chiếc Mi MIX 3 đều được tặng k&egrave;m đế sạc kh&ocirc;ng d&acirc;y 10W của Xiaomi.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/10/Mi-mix-3-8.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 13090000, 12990000, 30, '#000000', 'Snapdragon 845 8 nhân', '6GB', 'AMOLED 6,39 inch, độ phân giải Full HD+', 'Android 9', 8, NULL);
INSERT INTO `products` VALUES (65, 'Mi 8 Pro', 'mi-8-pro', 'Mi 8 Pro DGW (Global) không chỉ nổi bật với nhiều công nghệ đầu bảng hiện nay  cảm biến vân tay trong màn hình mà còn có thiết kế mặt lưng trong suốt độc đáo.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd81d8653df-Mi8Pro-1.jpg', '<h2><a href=\"https://mistore.com.vn/mi-8-pro-dgw/\">Mi 8 Pro DGW (Global)</a>&nbsp;kh&ocirc;ng chỉ nổi bật với nhiều c&ocirc;ng nghệ đầu bảng hiện nay&nbsp; cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh m&agrave; c&ograve;n c&oacute; thiết kế mặt lưng trong suốt độc đ&aacute;o.</h2>\r\n\r\n<blockquote>\r\n<p><em>Xiaomi Mi 8 PRO l&agrave; một điện thoại th&ocirc;ng minh Android h&agrave;ng đầu được ph&aacute;t triển bởi Xiaomi Inc. N&oacute; đ&atilde; được đưa ra tại một sự kiện được tổ chức tại Shenzen, Trung Quốc. (Wiki)</em></p>\r\n</blockquote>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-5.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 PRO trong suốt Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<h3>Thiết kế mặt k&iacute;nh trong suốt tr&ecirc;n Mi 8 PRO Global</h3>\r\n\r\n<p><strong>Mi 8 Pro phi&ecirc;n bản Global</strong>&nbsp;c&oacute; thiết kế kh&ocirc;ng kh&aacute;c g&igrave; phi&ecirc;n bản Mi 8 Explorer Edition (EE). Điểm nổi bật nhất cũng ch&iacute;nh l&agrave; điểm nhất của sản phẩm điện thoại Mi 8 Pro &ndash; mặt lưng trong suốt để lộ to&agrave;n bộ b&ecirc;n linh kiện b&ecirc;n trong.</p>\r\n\r\n<p>B&ecirc;n dưới mặt lưng k&iacute;nh nổi bật với board mạch xử l&yacute;, theo Xiaomi th&igrave; đ&acirc;y l&agrave; Board mạch thật, nhưng kh&ocirc;ng hoạt động.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-9.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 Pro DGW (Global)</p>\r\n\r\n<p>Thiết kế mặt lưng trong suốt kết hơp với c&aacute;c chi tiết th&acirc;n m&aacute;y, l&agrave;m thiết trở n&ecirc;n ph&aacute; c&aacute;ch 1 c&aacute;ch hiện đại.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh tai thỏ&nbsp;Xiaomi Mi 8 PRO Global</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-10.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 Pro DGW (Global) Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Th&ocirc;ng số Kỹ thuật</h3>\r\n\r\n<p>Mi 8 Pro phi&ecirc;n bản quốc tế ch&iacute;nh h&atilde;ng b&aacute;n ở Việt Nam l&agrave; bản c&oacute; mặt lưng trong suốt với RAM 8GB, bộ nhớ trong 128GB, vi xử l&yacute; Snapdragon 845 v&agrave; vi&ecirc;n pin 3000 mAh hỗ trợ sạc nhanh Quick Charge 4.0.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-1.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh&nbsp;Mi 8 Pro DGW (Global) Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<p>Cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh cũng l&agrave; điểm cộng của Mi 8 Pro khi n&oacute; nhanh, nhạy. Tuy nhi&ecirc;n, n&oacute; vẫn sẽ chậm hơn so với cảm biến v&acirc;n tay 1 chạm b&igrave;nh thường. N&oacute; c&oacute; camera v&agrave; đ&egrave;n hồng ngoại giống với &ldquo;l&otilde;m đen&rdquo; tr&ecirc;n Mi 8 để nhận diện tốt cả trong điều kiện tối, thiếu s&aacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-2.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 Pro DGW (Global) Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<p>Xiaomi Mi 8, Mi 8 PRO v&agrave; Mi 8 EE c&ograve;n l&agrave; chiếc smartphone đầu ti&ecirc;n được trang bị c&ocirc;ng nghệ GPS k&eacute;p. C&ocirc;ng nghệ n&agrave;y cho ph&eacute;p định vị ch&iacute;nh x&aacute;c với độ sai lệch chỉ 30 cm nhờ việc t&iacute;ch hợp đồng thời t&iacute;n hiệu GPS L1/ L5. So với GPS th&ocirc;ng thường độ sai lệch l&ecirc;n đến 5 m. Thực tế th&igrave; c&ocirc;ng nghệ n&agrave;y cũng được giới thiệu c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u n&ecirc;n việc chưa c&oacute; sản phẩm n&agrave;o được trang bị cũng l&agrave; điều dễ hiểu, trong tương lai phần lớn smartphone cũng sẽ được t&iacute;ch hợp.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-4.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 Pro DGW (Global) Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<h3>Camera Xiaomi Mi 8 Pro phi&ecirc;n bản quốc tế</h3>\r\n\r\n<p>Mi 8 Pro được trang bị cụm camera k&eacute;p, trong đ&oacute; một ống k&iacute;nh ch&iacute;nh 12 MP, khẩu độ f/1.8 v&agrave; ống k&iacute;nh phụ 12 MP, khẩu độ f/2.4. Camera của m&aacute;y được t&iacute;ch hợp AI, c&oacute; thể nhận diện vật thể khi chụp. Camera selfie của m&aacute;y c&oacute; độ ph&acirc;n giải 20 MP, khẩu độ f/2.0, hỗ trợ chế độ l&agrave;m đẹp nhiều cấp độ. Thử nghiệm nhanh cho thấy camera của Mi 8 Pro ở mức vừa phải, kh&ocirc;ng bằng với camera tr&ecirc;n Mi Mix 2S hay Mi Mix 3.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee.jpg\" style=\"height:640px; width:960px\" /></p>\r\n\r\n<p>H&igrave;nh Ảnh Mi 8 Pro DGW (Global) Ch&iacute;nh H&atilde;ng</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi8-ee-6.jpg\" style=\"height:640px; width:960px\" /></p>\r\n', 14090000, 13990000, 5, '#000000', 'Snapdragon 845 8 nhân', '8GB', 'Super AMOLED, 6.21″, Full HD+', 'Android 8.1 (Oreo)', 54, NULL);
INSERT INTO `products` VALUES (66, 'Mi 8 Lite', 'mi-8-lite', 'Xiaomi Mi 8 Lite có lẽ là chiếc smartphone rẻ nhất của dòng Mi (dòng flagship của Xiaomi) mà vẫn giữ được cho mình thiết kế đẹp mắt và nhiều tính năng thú vị.', 'https://mistorepro1013.000webhostapp.com/uploads/products/5cbd8334cfdc3-Mi8Lite-1.jpg', '<h2><a href=\"https://mistore.com.vn/mi-8-lite-dgw/\">Xiaomi Mi 8 Lite</a>&nbsp;c&oacute; lẽ l&agrave; chiếc&nbsp;smartphone&nbsp;rẻ nhất của d&ograve;ng Mi (d&ograve;ng flagship của Xiaomi) m&agrave; vẫn giữ được cho m&igrave;nh thiết kế đẹp mắt v&agrave; nhiều t&iacute;nh năng th&uacute; vị.</h2>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-10.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<h3>Thiết kế bắt mắt, thu h&uacute;t mọi &aacute;nh nh&igrave;n</h3>\r\n\r\n<p>Điểm nhấn tr&ecirc;n&nbsp;chiếc&nbsp;điện thoại Xiaomi&nbsp;n&agrave;y ch&iacute;nh l&agrave; phần mặt lưng với&nbsp;chất liệu k&iacute;nh với hiệu ứng gradient biến đổi m&agrave;u sắc t&ugrave;y theo mức độ &aacute;nh s&aacute;ng chiếu v&agrave;o. N&oacute; thực sự đẹp mắt v&agrave; ấn tượng mỗi khi nh&igrave;n v&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-7.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>C&oacute; 3 m&agrave;u sắc để bạn lựa chọn,&nbsp;&nbsp;Dream Blue, Twilight Gold và Đen v&agrave; c&oacute; 3 phi&ecirc;n bản Ram 4/64GB v&agrave; ram 6/64G bản cao nhất l&agrave; Ram 6/128GB. Sản phẩm vẫn được trang bị cổng kết nối USB-C v&agrave; cũng được loại bỏ jack cắm 3.5</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-3.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra Xiaomi cũng&nbsp;tối ưu c&aacute;ch sắp xếp phần cứng b&ecirc;n trong để đạt được độ mỏng chỉ 7.5mm cho điện thoại, nhằm mang lại trải nghiệm cầm nắm tốt hơn cho người d&ugrave;ng.</p>\r\n\r\n<h3>Camera Mi 8 LITE chất lượng kh&ocirc;ng k&eacute;m c&aacute;c m&aacute;y cao cấp</h3>\r\n\r\n<p>Mi 8 Lite gi&ocirc;́ng như m&ocirc;̣t phi&ecirc;n bản giá rẻ của Mi 8. Nó có camera kép 12 MP + 5 MP phía sau. Ở phía trước,&nbsp;Mi 8 Lite có camera l&ecirc;n tới 24 MP so với 20 MP tr&ecirc;n Mi 8, cải thi&ecirc;̣n khả năng chụp selfie cho người dùng trẻ tu&ocirc;̉i.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-2.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>Camera lần n&agrave;y được thiết kế ngang kh&aacute; giống với 5x. Theo như sản xuất th&igrave; camera trước cửa mi 8 lite sẽ sử dụng cảm biến của Sony mang lại cho người d&ugrave;ng chụp được những tấm h&igrave;nh ch&acirc;n thật nhất.</p>\r\n\r\n<h3>Cấu h&igrave;nh Mi 8 LITE &ndash; hiệu năng mạnh mẽ trong ph&acirc;n kh&uacute;c</h3>\r\n\r\n<p>Mi 8 Lite d&ugrave;ng chip Snapdragon 660 AIE (chuy&ecirc;n xử l&yacute; c&aacute;c t&aacute;c vụ li&ecirc;n quan đến tr&iacute; tuệ nh&acirc;n tạo) được sản xuất tr&ecirc;n quy tr&igrave;nh FinFET 10 nm, kết hợp với nh&acirc;n đồ họa Adreno 512.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-6.jpg\" style=\"height:640px; width:940px\" /></p>\r\n\r\n<p>M&aacute;y vẫn sở hữu cho m&igrave;nh tới 4 GB RAM v&agrave; 64 GB bộ nhớ trong mang lại trải nghiệm mượt m&agrave; với hầu hết c&aacute;c t&aacute;c vụ hằng ng&agrave;y.</p>\r\n\r\n<p>Vi&ecirc;n&nbsp;pin dung lượng 3.250 mAh với sạc nhanh Qualcomm Quick Charge 4.0 đủ đ&aacute;p ứng cho bạn nhu cầu sử dụng trong cả ng&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"http://mistore.com.vn/wp-content/uploads/2018/09/mi-8-lte-9.jpg\" style=\"height:640px; width:940px\" /></p>\r\n', 6690000, 5990000, 70, '#000000', 'Snapdragon 660 8 nhân', '4GB', 'Super AMOLED, 6.26″, Full HD+', 'Android 8.1 (Oreo)', 40, 3.67);

-- ----------------------------
-- Table structure for sliders
-- ----------------------------
DROP TABLE IF EXISTS `sliders`;
CREATE TABLE `sliders`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `sort_order` int(11) NULL DEFAULT NULL,
  `image_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `short_desc` varchar(500) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

