-- ----------------------------
-- Triggers structure for table comments
-- ----------------------------
DROP TRIGGER IF EXISTS `starAVGIns`;
delimiter ;;
CREATE TRIGGER `starAVGIns` AFTER INSERT ON `comments` FOR EACH ROW BEGIN
            UPDATE products
            SET star = (SELECT AVG(star) FROM comments
                         WHERE products.id = comments.product_id);
            END
;;
delimiter ;

